import React, {useState,useContext} from "react";
import { LikeFilled,CommentOutlined,LikeOutlined } from "@ant-design/icons";
import { Image,Card, Dropdown } from "react-bootstrap"
import axiosService from "../../helpers/axios";
import MoreToggleIcon from "../MoreToggleIcon";
import { getUser } from "../../hooks/user.actions";
import UpdatePost from "./UpdatePost";
import { Link } from "react-router-dom";
import { Context } from "../Layout";
import {Button,Modal, Form} from "react-bootstrap";
import CreateComment from "../comments/CreateComment";
import Comment from "../comments/Comment";


function Post(props) {
    const {post, refresh, isSinglePost, postId} = props;
    const { setToaster } = useContext(Context);
    const user = getUser();
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [validated,setValidated] = useState(false);
    const [show,setShow] = useState(false);
    const [form,setForm] = useState({
        author: "",
        body: "",
        post: "",})
        const handleSubmit = (event) => {
            event.preventDefault();
            const createCommentForm = event.currentTarget;
            if (createCommentForm.checkValidity()===false)
                {
                 event.stopPropagation()
                }
            setValidated(true);
            const data = {
                author: user.id,
                body: form.body,
                post: postId,
            };
    
            axiosService.post(`/api/post/${postId}/comment/`,data).then(() => {
                setForm({...form,body:""});
                setToaster({
                    type: "succcess",
                    message: "Comment Posted successfully",
                    show: true,
                    title: "Comment!"
                });
                refresh();
                }).catch( () => { setToaster({
                    type: "danger",
                    message: "",
                    show: true,
                    title: "an error occured",
                    });
                });
             };
    const handleCommentClick = () => {
        return(
        <>
        <Form.Group className = "my-3 w-75">
          <Form.Control className = "py-2rounded-pill border-primary" type="text" placeholder="write a post" onClick={handleShow}/>
        </Form.Group>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton className="border-0">
                <Modal.Title>Create Post</Modal.Title>
                </Modal.Header>
                <Modal.Body className="border-0">
                <Form className="d-flex flex-row justify-content-between" noValidate validated={validated} onSubmit={handleSubmit}>
            <Image src = {user.avatar} roundedCircle width={48} height={48} className="my-2" />
            <Form.Group className="m-3 w-75">
                <Form.Control className='py-2 rounded-pill border-primary' type='text' placeholder="write a comment" 
                 value={form.body} name='body' onChange={(e) =>setForm({...form,body:e.target.value})} />
            </Form.Group>
            <div className="m-auto">
                <Button variant="primary" onClick={handleSubmit} disabled={!form.body} size='small'>
                    comment
                </Button>
            </div>
    
            </Form>
                </Modal.Body>
                <Modal.Footer>
                <Button
                    variant="primary"
                    onClick={handleSubmit}
                    disabled={!form.body}
                    data-testid="create-post-submit"
                >
                    Post
                </Button>
                </Modal.Footer>
            </Modal>
            </>
        );


    }
    const handleLikeClick = (action) => {
        axiosService.post(`/api/post/${post.id}/${action}/`).then(() => {
            refresh();
        }).catch((err) => console.error(err));
    };
    const handleDelete = () => {
        axiosService.delete(`/api/post/${post.id}/`).then(() => {
            setToaster({
                type: "warning",
                message: "Post deleted ðŸš€",
                show: true,
                title: "Post Deleted",
              });
            
            refresh();
        }).catch(() => {
            setToaster({
                type: "danger",
                message: "An error occurred.",
                show: true,
                title: "Post Error",
              });
        });
    };
    return (
        <>
            <Card className="rounded-3 my-4">
                <Card.Body>
                    <Card.Title className="d-flex flex-row justify-content-between">
                        <div className="d-flex flex-row">
                        <Image
                            src={user.avatar}
                            roundedCircle
                            width={48}
                            height={48}
                            className="me-2 border border-primary border-2"
                        />
                        <div className="d-flex flex-column justify-content-start align-self-center mt-2">
                            <p className="fs-6 m-0">{post.author.name}</p>
                            <p className="fs-6 fw-lighter">
                            <small>{post.created}</small>
                            </p>
                        </div>
                        </div>
                        {user.name === post.author.name && (
                        <div>
                            <Dropdown>
                            <Dropdown.Toggle as={MoreToggleIcon} />
                            <Dropdown.Menu>
                                <UpdatePost post={post} refresh={refresh} />
                                <Dropdown.Item
                                onClick={handleDelete}
                                className="text-danger"
                                >
                                Delete
                                </Dropdown.Item>
                            </Dropdown.Menu>
                            </Dropdown>
                        </div>
                        )}
                    </Card.Title>
                    <Card.Text>{post.body}</Card.Text>
                   
                    <div className="d-flex flex-row">
                    <LikeFilled
                        style={{
                        color: "#fff",
                        backgroundColor: "#0D6EFD",
                        borderRadius: "50%",
                        width: "18px",
                        height: "18px",
                        fontSize: "75%",
                        padding: "2px",
                        margin: "3px",
                        }}
                    />
                    <p className="ms-1 fs-6">
                        <small>{post.likes_count} like</small>
                    </p>
                    </div>
                    {!isSinglePost && (
                        <p className="ms-1 fs-6">
                            <small>
                            <Link to={`/post/${post.id}/`}>
                                {post.comments_count} comments
                            </Link>
                            </small>
                        </p>         )
                    }
            
                </Card.Body>
                <Card.Footer className="d-flex bg-white w-50 justify-content-between border-0">
                    <div className="d-flex flex-row">
                        <LikeOutlined
                        style={{
                            width: "24px",
                            height: "24px",
                            padding: "2px",
                            fontSize: "20px",
                            color: post.liked ? "#0D6EFD" : "#C4C4C4",
                        }}
                        onClick={() => {
                            if (post.liked) {
                            handleLikeClick("remove_like");
                            } else {
                            handleLikeClick("like");
                            }
                        }}
                        />
                        <p className="ms-1">
                        <small>Like</small>
                        </p>
                    </div>
                    {!isSinglePost && (
                    <div className="d-flex flex-row">
                        <CommentOutlined
                            style={{
                            width: "24px",
                            height: "24px",
                            padding: "2px",
                            fontSize: "20px",
                            color: "#C4C4C4",
                            }}
                            onClick={() => {
                                handleCommentClick();
                                
                            }}
                        />
                        <p className="ms-1 mb-0">
                            <small>Comment</small>
                        </p>
                        </div>
                    )}
                </Card.Footer>
            </Card>
            
        </>
    );
}

export default Post;
