import React, {useState} from "react";
import {Form,Button} from "react-bootstrap";
import axios from "axios";
import {useNavigate} from "react-router-dom";

function RegistrationForm() {
    const navigate = useNavigate();
    const [validated,setValidated] = useState(false);
    const [form,setForm] = useState({});
    const [error,setError] = useState(null);

    const handleSubmit = (event) => {
        event.preventDefault();
        const registrationForm = event.currentTarget;
        if (registrationForm.checkvalidity() === false) {
            event.stopPropagation();
        }
        setValidated(true);
        const data = {
            username: form.username,
            password: form.password,
        };

        axios.post("http://localhost:8000/api/signup/",data).then((res) => {
            //registering account token
            localStorage.setItem("auth",JSON.stringify({
                access: res.data.access,
                refresh: res.data.refresh,
                username: res.data.username,
            }));
            navigate("/");
        }).catch((err) => {
            if (err.message) {
                setError(err.request.response);
            };
        });
    };
    return (
        <Form id = 'registration-form'
        className = 'border p-4 rounded'
        noValidate
        validated = {validated}
        onSubmit={handleSubmit}
        >
            <Form.Group className = "mb-3">
                <Form.Label> username </Form.Label>
                <Form.Control value = {form.username} onChange ={(e) => setForm({form,username:e.target.value})}
                required type = 'text' placeholder = "enter username"/>
                <Form.Control.Feedback type = "invalid"> This field is required</Form.Control.Feedback>
            </Form.Group>

            <Form.Group className = "mb-3">
                <Form.Label> username </Form.Label>
                <Form.Control value = {form.password} minLength="8" onChange ={(e) => setForm({form,password:e.target.value})}
                required type = 'text' placeholder = "enter password"/>
                <Form.Control.Feedback type = "invalid"> This field is required</Form.Control.Feedback>
            </Form.Group>
            <div className = 'text-context text-danger'>
                {error && <p>{error}</p>}
            </div>
            <Button variant = 'primary' type = "submit">Log in</Button>
        </Form>
    );
}
export default RegistrationForm
