import React from "react";
import Layout from "../components/Layout";
import { Row, Col, Image } from "react-bootstrap";
import useSWR from "swr";
import { fetcher } from "../helpers/axios";
import { getUser } from "../hooks/user.actions";
import  Post  from "../components/posts/Post";
import CreatePost from "../components/posts/CreatePost";
import { randomAvatar } from "../utils";
import ProfileCard from "../components/profile/ProfileCard";


function Home() {
    const posts = useSWR("/post/",fetcher,{refreshInterval: 10000,});
    const profiles = useSWR("/user/?limit=5", fetcher);
    const user = getUser();
    if(!user) {
        return <div> Loading </div>;
    }
    
    return(
        
        <Layout>
            <Row className="justify-content-evenly">
                <Col sm={7}>
                    <Row className="border rounded align-items-center">
                        <Col className="flex-shrink-1">
                            <Image src = {randomAvatar()} roundedCircle width ={52} height ={52} className="my-2"/>
                        </Col>
                        <Col sm={10} className="flex-grow-1">
                            <CreatePost />
                        </Col>
                    </Row>
                    <Row className="my-4">
                        {posts.data?.results.map((post,index) => ( <Post key = {index} post = {post} refresh = {post.mutate} /> ))}
                    </Row>
                </Col>
                <Col sm={3} className="border rounded py-4 h-50">
          <h4 className="font-weight-bold text-center">Suggested people</h4>
          <div className="d-flex flex-column">
            {profiles.data &&
              profiles.data.results.map((profile, index) => (
                <ProfileCard key={index} user={profile} />
              ))}
          </div>
        </Col>
            </Row>
        
        </Layout>
      
    );
}
export default Home;
