import React, { createContext, useMemo, useState } from "react";
import NavigationBar from "./Navbar";
import Calendar from "react-calendar";
import { ArrowLeftOutlined } from "@ant-design/icons";
import { useNavigate } from "react-router-dom";
import Toaster from "./Toaster";
import 'react-calendar/dist/Calendar.css';
import ReactCalendar from "./calendar";


export const Context = createContext("unknown");

function Layout(props) {
    const navigate = useNavigate();
    const [toaster, setToaster] = useState({
    title: "",
    show: false,
    message: "",
    type: "",
    });
    const [entry, onChange] = useState(new Date());
  
    const value = useMemo(() => ({ toaster, setToaster }), [toaster]);
    const { hasNavigationBack } = props;
    return(
        <Context.Provider value={value}>
        <div>
          <NavigationBar />
          {hasNavigationBack && (
            <ArrowLeftOutlined
              style={{
                color: "#0D6EFD",
                fontSize: "24px",
                marginLeft: "5%",
                marginTop: "1%",
              }}
              onClick={() => navigate(-1)}
            />
          )}
          <div className="container my-2"> {this.props} </div>
        </div>
        <Toaster
          title={toaster.title}
          message={toaster.message}
          type={toaster.type}
          showToast={toaster.show}
          onClose={() => setToaster({ ...toaster, show: false })}
        />
       <div className="container my-2"> <ReactCalendar onChange={onChange} value={entry} /> </div>
      </Context.Provider>
      

    );
}
export default Layout;
