import React, { useState, useContext } from "react";
import { Button, Modal, Form, Dropdown } from "react-bootstrap";
import axiosService from "../../helpers/axios";

import { Context } from "../Layout";



function UpdateComment(props) {


    const { postId, comment, refresh } = props;
    const [show, setShow] = useState(false);
    const [validated, setValidated] = useState(false);
    const [form, setForm] = useState({
        author: comment.author.id,
        body: comment.body,
        post: postId,
    });
    const { setToaster } = useContext(Context);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
  


    const handleSubmit = (event) => {
        event.preventDefault();
        const updateCommentForm = event.currentTarget;
        if (updateCommentForm.checkValidity === false) {
            event.stopPropagation();
        };
        setValidated(true);
        const data = {
            author: form.author,
            body: form.body,
            post: postId,
        };
        axiosService.put(`/api/post/${postId}/comment/${comment.id}`,data).then(() => {
            handleClose();
            setToaster({
                type: "success",
                message: "comment updated",
                show: true,
                title: "success!"
            });
            refresh();
        }).catch((error) => {
            setToaster({
                type: "danger",
                message: "An error occured",
                show: true,
                title: "comment error",
            });
        });

    }

    return (
        <>
          <Dropdown.Item data-testid="show-modal-form" onClick={handleShow}>
            Modify
          </Dropdown.Item>
    
          <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton className="border-0">
              <Modal.Title>Update Post</Modal.Title>
            </Modal.Header>
            <Modal.Body className="border-0">
              <Form
                data-testid="update-comment-test"
                noValidate
                validated={validated}
                onSubmit={handleSubmit}
              >
                <Form.Group className="mb-3">
                  <Form.Control
                    name="body"
                    value={form.body}
                    data-testid="comment-body-field"
                    onChange={(e) => setForm({ ...form, body: e.target.value })}
                    as="textarea"
                    rows={3}
                  />
                </Form.Group>
              </Form>
            </Modal.Body>
            <Modal.Footer>
              <Button
                data-testid="update-comment-submit"
                variant="primary"
                onClick={handleSubmit}
              >
                Modify
              </Button>
            </Modal.Footer>
          </Modal>
        </>
      );
}

export default UpdateComment;
