import React from "react";
import Layout from "../components/Layout";
import {Row,Col} from "react-bootstrap";
import { useParams, useparams } from "react-router-dom";
import useSWR from "swr";
import { fetcher } from "../helpers/axios";
import { Post } from "../components/posts";
import CreateComment from "../components/comments/CreateComment";

function SinglePost() {
    console.log(useParams())
    let {postId} = useParams();
    const post = useSWR(`/post/${post.id}/`,fetcher);
    const comments = useSWR(`/post/${post.id}/comments/`,fetcher);
    return (
        <Layout hasNavigationBack>
            {post.data ? (
                <Row className= "justify-content-center">
                    <col sm={8}>
                        <Post post={post.data} refresh={post.mutate} isSinglePost/>
                         console.log('post id data',post.data.id)
                        <CreateComment postId={post.data.id} refresh={comments.mutate} />

                    </col>
                </Row>
            ): (
                <div> Loading...</div>
            )}
        </Layout>
    );
}
export default SinglePost;
