import React, { useState,useContext } from "react";
import { Button,Form,Image } from "react-bootstrap";
import axiosService from "../../helpers/axios";
import { getUser } from "../../hooks/user.actions";
import { randomAvatar } from "../../utils";
import UpdateComment from "./UpdateComment";
import { Context } from "../Layout";
import {MoreToggleIcon} from "../MoreToggleIcon";

function CreateComment(props) {
    const {postId,comment, refresh} = props;
    const [avatar,setAvatar] = useState(randomAvatar())
    const [validated,setValidated] = useState(false);
    const [form,setForm] = useState({})
    const {tosster,setToaster} = useContext(Context)
    const user = getUser();
    const handleSubmit = (event) => {
        event.preventDefault();
        const createCommentForm = event.currentTarget;
        if (createCommentForm.checkValidity()==false)
            {
             event.stopPropagation()
            }
        setValidated(true);
        const data = {
            author: user.id,
            body: form.body,
            post: postId,
        };

        axiosService.post(`/post/${postId}/comment/`,data).then(() => {
            setForm({...form,body:""});
            setToaster({
                type: "succcess",
                message: "Comment Posted successfully",
                show: true,
                title: "Comment!"
            });
            refresh();
            }).catch( () => { setToaster({
                type: "danger",
                message: "",
                show: true,
                title: "an error occured",
                });
            });
         };

         const handleDelete = (event) => {
            axiosService.delete()

         }

    return (
        <Form className="d-flex flex-row justify-content-between" noValidate validated={validated} onSubmit={handleSubmit}>
            <Image src = {avatar} roundedCircle width={48} height={48} className="my-2">
            <Form.Group className="m-3 w-75">
                <Form.Control className='py-2 rounded-pill border-primary' type='text' placeholder="write a comment" 
                 value={form.body} name='body' onChange={(e) =>setForm({...form,body:e.target.value})} />
            </Form.Group>
            <div className="m-auto">
                <Button variant="primary" onClick={handleSubmit} disabled={form.body==undefined} size='small'>
                    comment
                </Button>
            </div>
            </Image>


        </Form>
    );
}
export default CreateComment;
