import { PlusOutlined } from "@ant-design/icons";
import React, { useState,useContext } from "react";
import { Button, Modal, Form, Dropdown } from "react-bootstrap";
import axiosService from "../../helpers/axios";
import { Context } from "../Layout";


function UpdatePost(props) {
    const {post,refresh} = props;
    const [show,setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [form,setForm] = useState({
        author: comment.author.id,
        body: comment.body,
        post: postId,
    });
    const {toaster,setToaster} = useContext(Context);
    const handleSubmit = (event) => {
        event.preventDefault();
        const updateCommentForm = event.currentTarget;
        if (updateCommentForm.checkValidity == false) {
            event.stopPropagation();
        };
        setValidated(true);
        const data = {
            author: form.author,
            body: form.body,
            post: PostId,
        };
        axiosService.put(`/post/${postId}/comment/${comment.id}`,data).then(() => {
            handleClose();
            setToaster({
                type: "success",
                message: "comment updated",
                show: true,
                title: "success!"
            });
            refresh();
        }).catch((error) => {
            setToaster({
                type: "danger",
                message: "An error occured",
                show: true,
                title: "comment error",
            });
        });
    }
    return (
        <>
          <Dropdown.Item onClick = {handleShow}> Modify </Dropdown.Item>
          <Modal show= {show} onHide= {handleClose}>
            <Modal.Header closeButton className="border-0">
            <Modal.Title>Update Post</Modal.Title>
            </Modal.Header>
            <Modal.Body className="border-0">
                <Form
                    noValidate
                    validated={validated}
                    onSubmit={handleSubmit}
                    data-testid="update-post-form"
                >
                    <Form.Group className="mb-3">
                    <Form.Control
                        name="body"
                        value={form.body}
                        data-testid="post-body-field"
                        onChange={(e) => setForm({ ...form, body: e.target.value })}
                        as="textarea"
                        rows={3}
                    />
                    </Form.Group>
                </Form>
            </Modal.Body>
            <Modal.Footer>
            <Button
                data-testid="update-post-submit"
                variant="primary"
                onClick={handleSubmit}
            >
                Modify
            </Button>
            </Modal.Footer>
          </Modal>
        
        </>
    );
};
export default UpdatePost;
